import logo from './logo.svg';
import './App.css';
import ReactDOM from 'react-dom';
import {
  ComposableMap,
  Geographies,
  Geography,
  Marker,
  ZoomableGroup,
} from "react-simple-maps";import React from 'react';
import ProgressBar from 'react-bootstrap/ProgressBar'

const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

const colorScale = scaleLinear()
  .domain([0.29, 0.68])
  .range(["#ffedea", "#ff5233"]);

const App = () => (  

  <div id="mainContainer">

  <div id="top">

    <h1>Covid-19 Vaccination Tracker</h1>
  </div>
  <div id="mapContainer">
    <ComposableMap 
      width="1200" 
      style={{ width: "80%"}} 
      data-tip="" 
      projectionConfig={{ scale: 200 }} 
      id="map">

      <Geographies geography={geoUrl}>
        {({ geographies }) =>
          geographies.map(geo => <Geography 
                                    key={geo.rsmKey} 
                                    geography={geo} 
                                    style={{
                                      default: {
                                        fill: "#ECEFF1",
                                        stroke: "#607D8B",
                                        strokeWidth: 1,
                                        outline: "none"
                                      },
                                      hover: {
                                        fill: "#ECEFF1",
                                        stroke: "#007575",
                                        strokeWidth: 1,
                                        outline: "none"
                                      },
                                      pressed: {
                                        fill: "#ECEFF1",
                                        stroke: "#007575",
                                        strokeWidth: 1,
                                        outline: "none"
                                      },
                                    }}
                                    />)
        }
      </Geographies>
    </ComposableMap>
  </div>
  </div>
);

export default App;
