import './MapChart.css';
import React, { memo } from 'react';
import { useEffect, useState } from "react";
import { csv } from "d3-fetch";
import * as d3 from 'd3';
import ReactToolTip from "react-tooltip";
import {
  ComposableMap,
  Geographies,
  Geography,
  Marker,
  ZoomableGroup,
} from "react-simple-maps";

const colorScale = d3.scaleLinear()
  .domain([0, 10, 30, 100])
  .range(['#F8EEFF', '#9969C7', '#804FB3', '#552586']);


const url = "https://raw.githubusercontent.com/owid/covid-19-data/master/public/data/vaccinations/vaccinations.csv";

const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";


const MapChart = ({
  setTooltipContent,
  setSelectedCountry,
  selectedCountry,
}) => { 

  const [data, setData] = useState([]);

  useEffect(() => {
      csv(url).then((data) => {
        setData(data);
      });
    }, []);



  return (
    <div id="mainContainer">
    <div id="top">
      <h1>Covid-19 Vaccination Tracker</h1>

    </div>
    <div id="mapContainer">
      <ComposableMap 
        width="1200" 
        style={{ width: "100%"}} 
        data-tip="" 
        projectionConfig={{ scale: 200 }} 
        id="map">
        <ZoomableGroup zoom={1}>
          <Geographies geography={geoUrl}>
            {({ geographies }) =>
              geographies.map((geo) => {
                const d = data.slice().reverse().find((s) => s.iso_code === geo.properties.ISO_A3);
                return (
                  <Geography
                    key={geo.rsmKey}

                    onMouseEnter={() => {
                      setTooltipContent("");
                    }}


                    geography={geo}
                    fill={d ? colorScale(d["people_vaccinated_per_hundred"]) : "#F5F4F6"}
                    style={{
                      default: {
                        stroke: "#808080",
                        strokeWidth: 0.75,
                        outline: "none"
                      },
                      hover: {
                        stroke: "#808080",
                        strokeWidth: 2,
                        outline: "none"
                      },
                      pressed: {
                        stroke: "#808080",
                        strokeWidth: 2,
                        outline: "none"
                      }
                    }}
                  />
                );
              })
            }
          </Geographies>
        </ZoomableGroup>
      </ComposableMap>
    </div>
    </div>
  );
};

export default MapChart;
