import './App.css';
import React from 'react';
import { useEffect, useState } from "react";
import { csv } from "d3-fetch";
import * as d3 from 'd3';
import {
  ComposableMap,
  Geographies,
  Geography,
  Marker,
  ZoomableGroup,
} from "react-simple-maps";

const colorScale = d3.scaleLinear()
  .domain([0, 1])
  .range(['#E6E6FA','#4B0082']);

const App = () => { 

  const url = "https://raw.githubusercontent.com/owid/covid-19-data/master/public/data/vaccinations/vaccinations.csv";

  const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

  const [data, setData] = useState([]);

  useEffect(() => {
      csv(url).then((data) => {
        setData(data);
      });
    }, []);



  return (
    <div id="mainContainer">
    <div id="top">
      <h1>Covid-19 Vaccination Tracker</h1>

    </div>
    <div id="mapContainer">
      <ComposableMap 
        width="1200" 
        style={{ width: "100%"}} 
        data-tip="" 
        projectionConfig={{ scale: 200 }} 
        id="map">

        <Geographies geography={geoUrl}>
          {({ geographies }) =>
            geographies.map((geo) => {
              const d = data.find((s) => s.iso_code === geo.properties.ISO_A3);
              return (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}
                  fill={d ? colorScale(d["people_vaccinated_per_hundred"]) : "#F5F4F6"}
                  style={{
                    default: {
                      stroke: "#607D8B",
                      strokeWidth: 1,
                      outline: "none"
                    },
                    hover: {
                      stroke: "#008080",
                      strokeWidth: 1,
                      outline: "none"
                    },
                    pressed: {
                      stroke: "#008080",
                      strokeWidth: 1,
                      outline: "none"
                    }
                  }}
                />
              );
            })
          }
        </Geographies>
      </ComposableMap>
    </div>
    </div>
  );
};

export default App;
